import React from "react";
import TableView from "react-table-view";

// table-view: https://www.npmjs.com/package/react-table-view

function Table(props) {
  let counter = 1;
  const listItems = props.rankedTeams.map((teamObject) => (
    <tr key={teamObject.name}>
      <th>{counter++}</th>
      <th>{teamObject.name.toUpperCase()}</th>
      <th>{teamObject.points}</th>
      <th>
        {teamObject.appearances}/{props.rankersCount}
      </th>
    </tr>
  ));
  const DATA = [
    { id: 0, make: "Honda", model: "NSX", year: "1997" },
    { id: 1, make: "Toyota", model: "Supra", year: "1996" },
    { id: 2, make: "Nissan", model: "300ZX", year: "1998" },
  ];
  /* define the look of each column, OPTIONAL */
  let COLUMNS = {
    make: function (listItems) {
      return <span>{listItems.year}</span>;
    },
    model: function (data) {
      return <span>{data.model}</span>;
    },
    year: function (data) {
      return <p>{`Year: ${data.year}`}</p>;
    },
  };
  return (
    <div>
      <TableView data={listItems} columns={COLUMNS} />
      <table className="rankingsTable">
        <tbody>
          <tr>
            <th>Rank</th>
            <th>Team</th>
            <th>Points</th>
            <th>Top 20</th>
          </tr>
          {listItems}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
