import React from "react";

function Table(props) {
  let counter = 1;
  const listItems = props.rankedTeams.map((teamObject) => (
    <div>
      <div className="tblRankNum">#{counter++}</div>
      <div className="tblTeamName">{teamObject.name}</div>
      <div className="tblPoints">({teamObject.points} points)</div>
      <div className="tblAppear">{teamObject.appearances}</div>
    </div>
    // <tr key={teamObject.name}>
    //   <th>{counter++}</th>
    //   <th>{teamObject.name.toUpperCase()}</th>
    //   <th>{teamObject.points}</th>
    //   <th>
    //     {teamObject.appearances}/{props.rankersCount}
    //   </th>
    // </tr>
  ));
  return (
    <table className="rankingsTable">
      <tbody>
        <tr>
          <th>#</th>
          <th>Team</th>
          <th>Points</th>
          <th>Appearances</th>
        </tr>
        {listItems}
      </tbody>
    </table>
  );
}

export default Table;
